package com.tpg.tmjug.springdata.demo.mongodb.entity.converters;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;import com.tpg.tmjug.springdata.demo.mongodb.entity.EmailAddress;@Componentclass EmailAddressWriteConverter implements Converter<EmailAddress, String> {		public String convert(EmailAddress source) {		return source == null ? null : source.getValue();	}	}